INSERT INTO `indexpage` (`id`, `title`, `logo`, `slogan1`, `slogan2`, `adress`, `email`, `service`, `tel`, `build_year`, `kouhao`, `introduction`, `teacher_0`, `teacher_1`, `news_0`, `news_1`) VALUES
(1, '有家早教机构', 'http://localhost:80/my-site/img/logo.png', '爱的成长是一点一滴的积累', '教育的力量是一代代的传承', '浙江大学城市学院', '873056154@qq.com', '873056154', '15168278670', 2017, '有家早教机构 给予童年该有的爱', '有家早期教育建立了第一家早期教育机构，中国早期教育视频节目的市场正处于一个迫切需要科学化、规范化的阵痛时期。一方面是广大家长对孩子教育的重视和早期教育意识的觉醒，一方面则是成体系反应科学早教知识视频节目稀缺，不仅缺少科学早教的内容载体形式，还缺乏推广平台。尤其是央视曝光国内动画片含有暴力等不良内容，不适宜作为婴幼儿的早期教育视频使用。《积木宝贝闯世界》电视节目正是在这样的背景下应运而生，旨在向更广大的家庭传播科学的、系统的早期教育知识，以及促进整个早教视频行业的规范化发展。 ', '名师专栏', '十年树木，十载风，十载雨，十万栋梁。', '动态', '爱的成长是一点一滴的积累，教育的力量是一代代的传承');

